<?phpclass Date {	public static function getCurrentDateSQL() {		return date('Y-m-d H:i:s');	}	public static function addSeconds($date,$second,$format = 'Y-m-d H:i:s') {		$currentDate = strtotime($date);		$futureDate = $currentDate+$second;		return date($format, $futureDate);	}	public static function time2SqlDate($time,$format = 'Y-m-d H:i:s') {		return date($format, $time);	}}