<?phpclass Validate {	public static function validateAddress($data){		if(!isset($data['firstname']) || empty($data['firstname'])) return false;		if(!isset($data['lastname']) || empty($data['lastname'])) return false;		if(!isset($data['address']) || empty($data['address'])) return false;		if(!isset($data['city']) || empty($data['city'])) return false;		if(!isset($data['country']) || empty($data['country'])) return false;		if(!isset($data['zipcode']) || empty($data['zipcode'])) return false;		if(!isset($data['phone']) || empty($data['phone'])) return false;		return true;	}		public static function validateEmail($email){		if(is_email($email) == false) return 'Email address is invalid.';		require_once 'customer.php';		$customer = Customer::findByEmail($email);		if($customer != null) return 'Email "'.$email.'" has been registed before.';		return '';	}	}