<?php/*Template Name: Index*/get_header(); $args = array(   'post_type' => 'attachment',   'numberposts' => -1,   'post_status' => null,   'orderby' => 'menu_order',   'order'          => 'ASC',   'post_parent' => $post->ID);$attachments = get_posts( $args );?>	<img class="bg" src="<?php bloginfo('template_url'); ?>/images/bg/index.jpg">	<div id="horizontal_slider">	<table cellpadding="0">		<tr>			<?php			if ( $attachments ) {			foreach ( $attachments as $attachment ) {				$image = wp_get_attachment_image_src( $attachment->ID,'large' );				?>			<td>				<img src="<?php echo $image[0]?>" width="<?php echo $image[1]?>" style="max-height:480px"/>			</td>				<?php			}}			?>			<td>				<span id="back"></span>			</td>		</tr>	</table></div><?php get_footer(); ?><script type="text/javascript">$(document).ready(function(){		$("#horizontal_slider img").live("click",function(){		var scrollLeft = $("#horizontal_slider").scrollLeft();		$("#horizontal_slider").animate({scrollLeft: scrollLeft + $(this).offset().left }, 1000);	});	$("#horizontal_slider #back").live("click",function(){		$("#horizontal_slider").animate({scrollLeft: 0}, 700);	});});</script>