<?php/*Template Name: Press*/get_header(); $args = array(   'post_type' => 'attachment',   'numberposts' => -1,   'post_status' => null,   'orderby' => 'menu_order',   'order'          => 'ASC',   'post_parent' => $post->ID);$attachments = get_posts( $args );?>	<img class="bg" src="<?php bloginfo('template_url'); ?>/images/bg/press.jpg">	<div id="horizontal_slider">	<table cellpadding="0" id="press">		<tr>			<?php			if ( $attachments ) {			foreach ( $attachments as $attachment ) {				$image = wp_get_attachment_image_src( $attachment->ID,'large' );				$attachment_metadata = get_post_meta($attachment->ID, '_wp_attachment_metadata',true);				$image_link = '';				if(isset($attachment_metadata['image_meta']['image_link'])) {					$image_link = $attachment_metadata['image_meta']['image_link'];				}				?>			<td>				<a href="<?php echo $image_link?>" target="_blank" title="<?php echo $attachment->post_title ?>"><img src="<?php echo $image[0]?>" width="<?php echo $image[1]?>" style="max-height:480px"/></a>			</td>				<?php			}}			?>			<td>				<span id="back"></span>			</td>		</tr>	</table></div><?php get_footer(); ?><script>$(document).ready(function(){		$("#horizontal_slider #back").live("click",function(){		$("#horizontal_slider").animate({scrollLeft: 0}, 700);	});});</script>