<?php/** * The Template for displaying all single posts. * * @package WordPress * @subpackage Demo Shop * @since Demo Shop 1.0 */get_header(); ?>	<div id="primary">	<div id="content" role="main">		<div id="div_left">		<?php		$image = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), 'single-post-thumbnail' );		echo "<img src='$image[0]'/>";		?>		</div>		<div id="div_right">		<div class="title"><?php echo $post->post_title?></div>		<?php echo $post->post_content?><br/>		Price : $<?php echo get_post_meta(get_the_ID(),'catalog_product_price',true); ?><br/>		<?php 		$cart = $_SESSION['cart']==null?array():$_SESSION['cart'];		if(in_array($post->ID,$cart)) {			echo '<input type="button" value="Add to Cart" disabled="true" />';		} else {			echo '<input type="button" value="Add to Cart" onclick="addToCart('.$post->ID.',this)"/>';		}		?>		<br/>		<a href="<?php bloginfo('wpurl'); ?>">Continue shopping</a>		</div>			</div><!-- #content --></div><!-- #primary --><?php get_footer(); ?><script>function addToCart(product_id,this_) {	$(this_).attr('disabled','true');	$.ajax({		type : "GET",		cache: false,		url: "<?php echo get_bloginfo('wpurl'); ?>/ajax?action=add-to-cart&product_id="+product_id,		success : function(data){				if(data == 'EXIST') {				alert('This product has been added in cart!');				return;			}			if(data == 'OK') {				alert('Add in cart successful!');				return;			}		},		error: function(data){ 			alert('Error');			$(this_).removeAttr('disabled');		}				});}</script>