<?php/** * The Template for displaying all single posts. * * @package BamBooDev * @author LongNC * @since Shop 1.0 */$image = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), 'large' );get_header(); ?>	<?php get_sidebar('left'); ?><div style="float: right; width: 770px;"><?php if(function_exists('wp_content_slider')) { wp_content_slider(); } ?><div style="float: left; width: 100%; margin-top: 5px;">	<div id="center">		<h1><?php echo $post->post_title?></h1>		<div id="more_detail">		<?php		$content = $post->post_content;		$content = apply_filters('the_content', $content);		$content = str_replace(']]>', ']]&gt;', $content);		echo $content;		?>		</div>	</div>	<?php get_sidebar('right'); ?></div></div><?php get_footer(); ?>