<?php/** * The Template for displaying all single posts. * * @package BamBooDev * @author LongNC * @since Shop 1.0 */ $args = array(	'post_type' => 'attachment',	'numberposts' => 5,	'post_status' => null,	'post_parent' => $post->ID);$attachments = get_posts( $args );$image = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), 'medium' );get_header(); ?>	<?php get_sidebar('left'); ?><div id="right_col">	<div style="width: 100%" class="small_box_container">		<div class="header" style="text-align:left">Chi tiết sản phẩm</div>		<div class="content">			<div class="product_detail">				<div id="image_zone">					<div id="show">					<img style="max-width:500px" alt="<?php echo $post->post_title?>" src="<?php echo $image[0]?>" />					</div>					<div id="mini">						<?php						foreach($attachments as $attachment) {							$thumbnail = wp_get_attachment_image_src($attachment->ID, 'thumbnail');							$image = wp_get_attachment_image_src($attachment->ID, 'medium' );							echo '<img height="70" src="'.$thumbnail[0].'" data-ref="'.$image[0].'"/>';						}						?>					</div>				</div>				<div id="info">					<h1><?php echo $post->post_title?></h1>					<table style="width:100%">						<tbody>						<tr>							<td style="width:60px">Giá :</td>							<td><span class="font-bigger color-red"><?php echo format_number(get_post_meta($post->ID,'catalog_product_price',true)); ?></span> <b class="vnd">đ</b></td>						</tr>						<tr>							<td colspan="2" align="left" valign="center" height="100px">								<button type="button" title="Mua ngay" class="ui-buttonCart" id="AddToCart" data-ref-id="<?php echo $post->ID?>">								<span class="i-cart">Mua ngay</span>								</button>							</td>						</tr>					</tbody></table>				</div>				<div id="more_detail">				<?php				$content = $post->post_content;				$content = apply_filters('the_content', $content);				$content = str_replace(']]>', ']]&gt;', $content);				echo $content;				?>				</div>				<fieldset id="sendmail_zone">					<legend style="font-weight: bold;">Liên hệ chủ shop</legend>					<form id="sendmail" onsubmit="return false">						<input type="text" id="contact" value="" placeholder="Email hoặc số điện thoại của bạn ..."/><br/>						<textarea id="content" placeholder="Nội dung liên hệ..." rows="3"></textarea>						<input type="button" class="button" id="btSend" value="Gửi"/>					</form>				</fieldset>			</div>					</div>	</div></div><?php get_footer(); ?><script>//var pathname = window.location.pathname;$(document).ready(function(){	$("#mini img").live("click",function(){		$("#show img").attr("src",$(this).attr("data-ref"));	});		$("#AddToCart").click(function(){		var button = this;		var product_id = $(this).attr("data-ref-id");		button.disabled = true;		$.ajax({			type : "GET",			cache: false,			data : {				action : "add-to-cart",				product_id : product_id			},			url: "<?php echo get_bloginfo('wpurl'); ?>/ajax",			success : function(data){				button.disabled = false;				location.href = "<?php echo get_bloginfo('wpurl'); ?>/gio-hang";			}					});	});	$("#btSend").click(function(){		$("input[placeholder], textarea[placeholder]").each(function(){			if(this.value == $(this).attr('placeholder'))				this.value = '';		});		var contact = $("#sendmail #contact").val();		if(contact == "") {			alert("Vui lòng nhập thông tin liên hệ!");			$("#sendmail #contact").focus();			return;		}		var content = $("#sendmail #content").val();		if(content == "") {			alert("Vui lòng nhập nội dung cần gửi!");			$("#sendmail #content").focus();			return;		}		content = "Link : "+document.URL + "<br>Khách hàng : " + contact + "<br>Nội dung : "+content;		var button = this;		button.disabled = true;		$.ajax({			type : "POST",			cache: false,			data : {				action : "contact",				content : content			},			url: "<?php echo get_bloginfo('wpurl'); ?>/ajax",			success : function(data){				button.disabled = false;				if(data == 'OK') {					alert('Gửi yêu cầu liên hệ thành công!\nChúng tôi sẽ liên hệ lại với bạn trong thời gian sớm nhất.');					$("#sendmail #content").val("");					return;				}else if(data == 'EMPTY'){					alert('Có lỗi xảy ra, vui lòng thử lại!');					return;				}			},			error: function(data){ 				alert('Có lỗi xảy ra, vui lòng thử lại!');				button.disabled = false;			}					});	});	$('input[placeholder], textarea[placeholder]').placeholder();});</script>