<?phpdefine('POSTS_PER_PAGE',10);define('TEMPLATE_DIRECTORY',get_template_directory_uri());require_once(TEMPLATEPATH . "/meta-box.php");require_once(TEMPLATEPATH . "/meta-boxes.php");if (function_exists('add_theme_support')) {	add_theme_support( 'post-thumbnails' );	set_post_thumbnail_size( 175, 99999);	add_image_size('thumbnail', 175, 99999);	if ( function_exists( 'add_image_size' ) ) { 		add_image_size('large', 500, 99999);	}	//add_image_size('medium', 500, 9999);}function my_init() {	if (!is_admin()) {		if(DOMAIN == 'http://localhost/thuongmaidientu24h/') error_reporting(E_ALL);		wp_deregister_script('jquery');		wp_enqueue_script('jquery', TEMPLATE_DIRECTORY . '/js/jquery.js');		wp_enqueue_style( 'style',  TEMPLATE_DIRECTORY . "/style.css");	}}add_action('init', 'my_init');function init_sessions() {    if (!session_id()) {        session_start();		if(!isset($_SESSION['cart'])) $_SESSION['cart'] = array();    }}add_action('init', 'init_sessions');function debug($var,$exit = true){	echo '<pre>';	print_r($var);	if($exit) die;}function get_description($body){   $moreTag = '</strong>';   $content = '';   $morePos = mb_stripos($body, $moreTag,0,'UTF-8');   if ($morePos !== FALSE || $morePos > -1)   {      $content = mb_substr($body, 0,$morePos,'UTF-8');	  $content = str_replace('<strong>','',$content);   }   else      $content = $body;   return $content;}function format_date($date,$format='Y/m/d H:i:s') {	return date($format, strtotime($date));}function getParam($name,$default=''){	if(isset($_REQUEST[$name])) {		return strip_tags($_REQUEST[$name]);	}	return $default;}function getTagLink($tag) {	return '<a rel="tag" href="'.DOMAIN.'tag/'.$tag->slug.'" title="'.$tag->name.'">'.$tag->name.'</a>';}
?>