/*Table structure for table `wp_posts` */

DROP TABLE IF EXISTS `wp_posts`;

CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM AUTO_INCREMENT=20 DEFAULT CHARSET=utf8;

/*Data for the table `wp_posts` */

insert  into `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (1,1,'2013-09-04 14:20:40','2013-09-04 14:20:40','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','trash','open','open','','hello-world','','','2013-09-06 20:07:24','2013-09-06 20:07:24','',0,'http://localhost/thuongmaidientu24h/?p=1',0,'post','',1),(2,1,'2013-09-04 14:20:40','2013-09-04 14:20:40','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/thuongmaidientu24h/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','publish','open','open','','sample-page','','','2013-09-04 14:20:40','2013-09-04 14:20:40','',0,'http://localhost/thuongmaidientu24h/?page_id=2',0,'page','',0),(3,1,'2013-09-04 14:21:52','0000-00-00 00:00:00','','Auto Draft','','auto-draft','open','open','','','','','2013-09-04 14:21:52','0000-00-00 00:00:00','',0,'http://localhost/thuongmaidientu24h/?p=3',0,'post','',0),(4,1,'2013-09-04 18:36:24','2013-09-04 18:36:24','','Index','','publish','open','open','','index','','','2013-09-04 18:36:24','2013-09-04 18:36:24','',0,'http://localhost/thuongmaidientu24h/?page_id=4',0,'page','',0),(5,1,'2013-09-04 18:36:24','2013-09-04 18:36:24','','Index','','inherit','open','open','','4-revision-v1','','','2013-09-04 18:36:24','2013-09-04 18:36:24','',4,'http://localhost/thuongmaidientu24h/?p=5',0,'revision','',0),(6,1,'2013-09-06 17:59:11','0000-00-00 00:00:00','','Thương mại điện tử','','draft','open','open','','','','','2013-09-06 17:59:11','2013-09-06 17:59:11','',0,'http://localhost/thuongmaidientu24h/?p=6',0,'post','',0),(7,1,'2013-09-06 18:08:07','2013-09-06 18:08:07','<strong>Thương mại điện tử VN nhận được những nguồn đầu tư khổng lồ cả từ trong lẫn ngoài nước. Nhưng cái gì cũng có mặt trái của nó, việc nhận đầu tư cũng vậy.</strong>\r\n\r\nĐánh giá và phụ thuộc quá nhiều vào mức độ đầu tư\r\n\r\nLý do thì ai cũng hiểu bởi giá trị khoản đầu tư có lẽ đang là thông tin được các công ty công bố một cách... minh bạch nhất. Bên cạnh đó, tâm lý và thói quen \"tính giá trị\" và đánh giá dự án theo mức độ đầu tư cũng đang ảnh hưởng lớn thái độ nhìn nhận của thị trường với bản chất của một dự án. Đâu đó quan điểm: đầu tư nhiều là dự án tốt vẫn còn.\r\n\r\nPhải biết rằng, mức độ đầu tư là do con người định giá và sai số là rất lớn. Hơn nữa, con số mà các bạn nghe thấy như 60 triệu USD, <strong>20 triệu USD</strong> không có nghĩa là nhà đầu tư ném từng đó tiền vào dự án. Cam kết đơn thuần là cam kết và chỉ có giá trị tham khảo, đôi khi là giá trị truyền thông.\r\n\r\nVà câu chuyện của những dự án không chủ động về tài chính\r\n\r\n<a href=\"http://localhost/thuongmaidientu24h/wp-content/uploads/2013/09/121224nhom20mua.png\"><img class=\"alignnone size-medium wp-image-10\" alt=\"121224nhom20mua\" src=\"http://localhost/thuongmaidientu24h/wp-content/uploads/2013/09/121224nhom20mua-300x211.png\" width=\"300\" height=\"211\" /></a>\r\n\r\nĐầu tư không phải một món quà, nó là một cái dây buộc vào cổ bạn. Về luật pháp và cả thực tế, nhà đầu tư là những ông chủ thực sự của công ty, chứ không phải bạn dù bạn có đang là CEO đi chăng nữa. CEO của một công ty khá nổi tiếng trong ngành Thương mại điện tử Việt Nam từng ví nguồn đầu tư như \"dưỡng khí\" giúp bạn trong cuộc leo núi nhưng thực chất nó không khác một cái xích mà nhà đầu tư quảng qua cổ của doanh nghiệp và giúp \"nâng\" doanh nghiệp lên trong lúc nó kiệt sức. Nâng bạn lên nhưng họ có quyền và khả năng \"dắt\" bạn theo hướng có lợi cho họ. Rồi, thậm chí, khi mà họ đã chán, khả năng những NĐT này \"cắt xích\" cho bạn rơi tự do là hoàn toàn có thể.\r\n\r\nĐã có tiền lệ và đang diễn ra một lần nữa\r\n\r\nTrên thế giới đã có nhiều và Việt Nam vài năm gần đây cũng có một vài sự kiện cảnh tỉnh người ta về cái bẫy đầu tư. Hẳn các bạn còn nhớ việc công ty sở hữu Nhóm mua và Địa Điểm nhận khoản cam kết đầu tư lên tới 60 triệu USD từ các nhà đầu tư quốc tế. Khi đó, khoản tiền đầu tư lập tức giúp Nhóm mua trở nên mạnh mẽ và chiếm lĩnh vị trí số một trên thị trường mua theo nhóm tại Việt Nam. Chiến dịch tiền đè chết người khiến Nhóm mua không có đối thủ tại thời điểm đó nhưng lại trở thành nguyên nhân cái chết nhanh chóng đến đáng sợ của dịch vụ này khi mà một ngày các nhà đầu tư cảm thấy \"sợ hãi\" với cách điều hành của CEO kèm theo tương lai ảm đạm đã được báo trước của mô hình mua hàng theo nhóm. Nhà đầu tư quyết định lấy lại vị trí điều hành, và Nhóm mua đã trải qua một cơn sóng hỗn loạn, mà cho tới giờ vẫn chưa thể phục hồi như trước.\r\n\r\nVà bây giờ, vở kịch có nguy cơ diễn ra lại một lần nữa. Có nhiều dấu hiệu cho thấy một cuộc rút lui đang diễn ra. Vở kịch có vẻ vẫn như cũ, vẫn là những khúc mắc không thể giải quyết giữa CEO và nhà đầu tư khiến nguy cơ một cuộc chiến tranh quyền sở hữu tiếp tục diễn ra một lần nữa. Kết cục chỉ có thể là thay thế CEO hoặc công ty đó ra đi vì không có tiền tiếp tục.\r\n\r\nĐầu tư: Không phải phần thưởng\r\n\r\nMột sai lầm của đa số mọi người nghĩ đầu tư giống như người ta cho tiền bạn và đó là điều vui. Hoàn toàn không. Đầu tư đơn giản là mua bán quyền sở hữu công ty. Tệ hại hơn, nếu công ty bạn không sống được nhờ chính hoạt động của công ty mà dựa dẫm vào nguồn tài chính của các nhà đầu tư thì không khác gì bạn đang đặt mạng sống của bạn vào tay họ. Họ chẳng phải những thiên thần và nhiệm vụ của họ chẳng phải hỗ trợ bạn mà đơn giản là kiếm tiền từ bạn. Một khi mọi thứ êm đẹp thì không sao nhưng nếu bạn làm họ không vừa mắt, đơn giản bạn sẽ chết.\r\n\r\nĐáng tiếc, Thương mại điện tử Việt Nam vẫn đang có khá nhiều doanh nghiệp phải sống như vậy...','Thương mại điện tử VN - Nỗi khổ của kẻ sống nhờ đầu tư','','publish','open','open','','thuong-mai-dien-tu-vn-noi-kho-cua-ke-song-nho-dau-tu','','','2013-09-07 06:54:16','2013-09-07 06:54:16','',0,'http://localhost/thuongmaidientu24h/?p=7',0,'post','',0),(11,1,'2013-09-06 18:11:29','2013-09-06 18:11:29','Cái gì cũng có mặt trái của nó, việc nhận đầu tư cũng vậy.\r\n<!--more-->\r\n\r\nĐánh giá và phụ thuộc quá nhiều vào mức độ đầu tư\r\n\r\nLý do thì ai cũng hiểu bởi giá trị khoản đầu tư có lẽ đang là thông tin được các công ty công bố một cách... minh bạch nhất. Bên cạnh đó, tâm lý và thói quen \"tính giá trị\" và đánh giá dự án theo mức độ đầu tư cũng đang ảnh hưởng lớn thái độ nhìn nhận của thị trường với bản chất của một dự án. Đâu đó quan điểm: đầu tư nhiều là dự án tốt vẫn còn.\r\n\r\nPhải biết rằng, mức độ đầu tư là do con người định giá và sai số là rất lớn. Hơn nữa, con số mà các bạn nghe thấy như 60 triệu USD, 20 triệu USD không có nghĩa là nhà đầu tư ném từng đó tiền vào dự án. Cam kết đơn thuần là cam kết và chỉ có giá trị tham khảo, đôi khi là giá trị truyền thông.\r\n\r\nVà câu chuyện của những dự án không chủ động về tài chính\r\n\r\nĐầu tư không phải một món quà, nó là một cái dây buộc vào cổ bạn. Về luật pháp và cả thực tế, nhà đầu tư là những ông chủ thực sự của công ty, chứ không phải bạn dù bạn có đang là CEO đi chăng nữa. CEO của một công ty khá nổi tiếng trong ngành TMĐT Việt Nam từng ví nguồn đầu tư như \"dưỡng khí\" giúp bạn trong cuộc leo núi nhưng thực chất nó không khác một cái xích mà nhà đầu tư quảng qua cổ của doanh nghiệp và giúp \"nâng\" doanh nghiệp lên trong lúc nó kiệt sức. Nâng bạn lên nhưng họ có quyền và khả năng \"dắt\" bạn theo hướng có lợi cho họ. Rồi, thậm chí, khi mà họ đã chán, khả năng những NĐT này \"cắt xích\" cho bạn rơi tự do là hoàn toàn có thể.\r\n\r\nĐã có tiền lệ và đang diễn ra một lần nữa\r\n\r\nTrên thế giới đã có nhiều và Việt Nam vài năm gần đây cũng có một vài sự kiện cảnh tỉnh người ta về cái bẫy đầu tư. Hẳn các bạn còn nhớ việc công ty sở hữu Nhóm mua và Địa Điểm nhận khoản cam kết đầu tư lên tới 60 triệu USD từ các nhà đầu tư quốc tế. Khi đó, khoản tiền đầu tư lập tức giúp Nhóm mua trở nên mạnh mẽ và chiếm lĩnh vị trí số một trên thị trường mua theo nhóm tại Việt Nam. Chiến dịch tiền đè chết người khiến Nhóm mua không có đối thủ tại thời điểm đó nhưng lại trở thành nguyên nhân cái chết nhanh chóng đến đáng sợ của dịch vụ này khi mà một ngày các nhà đầu tư cảm thấy \"sợ hãi\" với cách điều hành của CEO kèm theo tương lai ảm đạm đã được báo trước của mô hình mua hàng theo nhóm. Nhà đầu tư quyết định lấy lại vị trí điều hành, và Nhóm mua đã trải qua một cơn sóng hỗn loạn, mà cho tới giờ vẫn chưa thể phục hồi như trước.\r\n\r\nVà bây giờ, vở kịch có nguy cơ diễn ra lại một lần nữa. Có nhiều dấu hiệu cho thấy một cuộc rút lui đang diễn ra. Vở kịch có vẻ vẫn như cũ, vẫn là những khúc mắc không thể giải quyết giữa CEO và nhà đầu tư khiến nguy cơ một cuộc chiến tranh quyền sở hữu tiếp tục diễn ra một lần nữa. Kết cục chỉ có thể là thay thế CEO hoặc công ty đó ra đi vì không có tiền tiếp tục.\r\n\r\nĐầu tư: Không phải phần thưởng\r\n\r\nMột sai lầm của đa số mọi người nghĩ đầu tư giống như người ta cho tiền bạn và đó là điều vui. Hoàn toàn không. Đầu tư đơn giản là mua bán quyền sở hữu công ty. Tệ hại hơn, nếu công ty bạn không sống được nhờ chính hoạt động của công ty mà dựa dẫm vào nguồn tài chính của các nhà đầu tư thì không khác gì bạn đang đặt mạng sống của bạn vào tay họ. Họ chẳng phải những thiên thần và nhiệm vụ của họ chẳng phải hỗ trợ bạn mà đơn giản là kiếm tiền từ bạn. Một khi mọi thứ êm đẹp thì không sao nhưng nếu bạn làm họ không vừa mắt, đơn giản bạn sẽ chết.\r\n\r\nĐáng tiếc, TMĐT Việt Nam vẫn đang có khá nhiều doanh nghiệp phải sống như vậy...','Thương mại điện tử VN - Nỗi khổ của kẻ sống nhờ đầu tư','','inherit','open','open','','7-revision-v1','','','2013-09-06 18:11:29','2013-09-06 18:11:29','',7,'http://localhost/thuongmaidientu24h/7-revision-v1/',0,'revision','',0),(8,1,'2013-09-06 18:08:07','2013-09-06 18:08:07','Cái gì cũng có mặt trái của nó, việc nhận đầu tư cũng vậy.\r\nLà một ngành còn khá mới mẻ, ngay cả các nước đang phát triển, TMĐT đang dần xâm nhập vào cuộc sống của người Việt và thay thế phần nào đó các phương thức giao dịch truyền thống. Được đánh giá là ngành có tiềm năng phát triển cực lớn trong tương lai nên không bất ngờ khi TMĐT nhận được những nguồn đầu tư khổng lồ cả từ trong lẫn ngoài nước. Nhưng cái gì cũng có mặt trái của nó, việc nhận đầu tư cũng vậy.\r\n\r\n\r\nĐánh giá và phụ thuộc quá nhiều vào mức độ đầu tư\r\n\r\n\r\nLý do thì ai cũng hiểu bởi giá trị khoản đầu tư có lẽ đang là thông tin được các công ty công bố một cách... minh bạch nhất. Bên cạnh đó, tâm lý và thói quen \"tính giá trị\" và đánh giá dự án theo mức độ đầu tư cũng đang ảnh hưởng lớn thái độ nhìn nhận của thị trường với bản chất của một dự án. Đâu đó quan điểm: đầu tư nhiều là dự án tốt vẫn còn.\r\n\r\n\r\n\r\n\r\nPhải biết rằng, mức độ đầu tư là do con người định giá và sai số là rất lớn. Hơn nữa, con số mà các bạn nghe thấy như 60 triệu USD, 20 triệu USD không có nghĩa là nhà đầu tư ném từng đó tiền vào dự án. Cam kết đơn thuần là cam kết và chỉ có giá trị tham khảo, đôi khi là giá trị truyền thông.\r\n\r\nVà câu chuyện của những dự án không chủ động về tài chính\r\n\r\nĐầu tư không phải một món quà, nó là một cái dây buộc vào cổ bạn. Về luật pháp và cả thực tế, nhà đầu tư là những ông chủ thực sự của công ty, chứ không phải bạn dù bạn có đang là CEO đi chăng nữa. CEO của một công ty khá nổi tiếng trong ngành TMĐT Việt Nam từng ví nguồn đầu tư như \"dưỡng khí\" giúp bạn trong cuộc leo núi nhưng thực chất nó không khác một cái xích mà nhà đầu tư quảng qua cổ của doanh nghiệp và giúp \"nâng\" doanh nghiệp lên trong lúc nó kiệt sức. Nâng bạn lên nhưng họ có quyền và khả năng \"dắt\" bạn theo hướng có lợi cho họ. Rồi, thậm chí, khi mà họ đã chán, khả năng những NĐT này \"cắt xích\" cho bạn rơi tự do là hoàn toàn có thể.\r\n\r\nĐã có tiền lệ và đang diễn ra một lần nữa\r\n\r\nTrên thế giới đã có nhiều và Việt Nam vài năm gần đây cũng có một vài sự kiện cảnh tỉnh người ta về cái bẫy đầu tư. Hẳn các bạn còn nhớ việc công ty sở hữu Nhóm mua và Địa Điểm nhận khoản cam kết đầu tư lên tới 60 triệu USD từ các nhà đầu tư quốc tế. Khi đó, khoản tiền đầu tư lập tức giúp Nhóm mua trở nên mạnh mẽ và chiếm lĩnh vị trí số một trên thị trường mua theo nhóm tại Việt Nam. Chiến dịch tiền đè chết người khiến Nhóm mua không có đối thủ tại thời điểm đó nhưng lại trở thành nguyên nhân cái chết nhanh chóng đến đáng sợ của dịch vụ này khi mà một ngày các nhà đầu tư cảm thấy \"sợ hãi\" với cách điều hành của CEO kèm theo tương lai ảm đạm đã được báo trước của mô hình mua hàng theo nhóm. Nhà đầu tư quyết định lấy lại vị trí điều hành, và Nhóm mua đã trải qua một cơn sóng hỗn loạn, mà cho tới giờ vẫn chưa thể phục hồi như trước.\r\n\r\n\r\n\r\nVà bây giờ, vở kịch có nguy cơ diễn ra lại một lần nữa. Có nhiều dấu hiệu cho thấy một cuộc rút lui đang diễn ra. Vở kịch có vẻ vẫn như cũ, vẫn là những khúc mắc không thể giải quyết giữa CEO và nhà đầu tư khiến nguy cơ một cuộc chiến tranh quyền sở hữu tiếp tục diễn ra một lần nữa. Kết cục chỉ có thể là thay thế CEO hoặc công ty đó ra đi vì không có tiền tiếp tục.\r\n\r\nĐầu tư: Không phải phần thưởng\r\n\r\nMột sai lầm của đa số mọi người nghĩ đầu tư giống như người ta cho tiền bạn và đó là điều vui. Hoàn toàn không. Đầu tư đơn giản là mua bán quyền sở hữu công ty. Tệ hại hơn, nếu công ty bạn không sống được nhờ chính hoạt động của công ty mà dựa dẫm vào nguồn tài chính của các nhà đầu tư thì không khác gì bạn đang đặt mạng sống của bạn vào tay họ. Họ chẳng phải những thiên thần và nhiệm vụ của họ chẳng phải hỗ trợ bạn mà đơn giản là kiếm tiền từ bạn. Một khi mọi thứ êm đẹp thì không sao nhưng nếu bạn làm họ không vừa mắt, đơn giản bạn sẽ chết.\r\n\r\nĐáng tiếc, TMĐT Việt Nam vẫn đang có khá nhiều doanh nghiệp phải sống như vậy...','Thương mại điện tử VN - Nỗi khổ của kẻ sống nhờ đầu tư','','inherit','open','open','','7-revision-v1','','','2013-09-06 18:08:07','2013-09-06 18:08:07','',7,'http://localhost/thuongmaidientu24h/7-revision-v1/',0,'revision','',0),(9,1,'2013-09-06 18:17:56','2013-09-06 18:17:56','Thương mại điện tử VN . Cái gì cũng có mặt trái của nó, việc nhận đầu tư cũng vậy.\n<!--more-->\n\nĐánh giá và phụ thuộc quá nhiều vào mức độ đầu tư\n\nLý do thì ai cũng hiểu bởi giá trị khoản đầu tư có lẽ đang là thông tin được các công ty công bố một cách... minh bạch nhất. Bên cạnh đó, tâm lý và thói quen \"tính giá trị\" và đánh giá dự án theo mức độ đầu tư cũng đang ảnh hưởng lớn thái độ nhìn nhận của thị trường với bản chất của một dự án. Đâu đó quan điểm: đầu tư nhiều là dự án tốt vẫn còn.\n\nPhải biết rằng, mức độ đầu tư là do con người định giá và sai số là rất lớn. Hơn nữa, con số mà các bạn nghe thấy như 60 triệu USD, 20 triệu USD không có nghĩa là nhà đầu tư ném từng đó tiền vào dự án. Cam kết đơn thuần là cam kết và chỉ có giá trị tham khảo, đôi khi là giá trị truyền thông.\n\nVà câu chuyện của những dự án không chủ động về tài chính\n\n<a href=\"http://localhost/thuongmaidientu24h/wp-content/uploads/2013/09/121224nhom20mua.png\"><img class=\"alignnone size-medium wp-image-10\" alt=\"121224nhom20mua\" src=\"ht