013-09-06 20:20:17','Test 2','Nest 2','','publish','open','open','','test-2','','','2013-09-06 21:20:23','2013-09-06 21:20:23','',0,'http://localhost/thuongmaidientu24h/?p=17',0,'post','',0),(18,1,'2013-09-06 20:20:34','2013-09-06 20:20:34','Test 3','Test 3','','publish','open','open','','test-3','','','2013-09-06 20:20:34','2013-09-06 20:20:34','',0,'http://localhost/thuongmaidientu24h/?p=18',0,'post','',0),(19,1,'2013-09-07 07:48:39','2013-09-07 07:48:39','','Tags Cloud','','publish','open','open','','tags-cloud','','','2013-09-07 07:48:39','2013-09-07 07:48:39','',0,'http://localhost/thuongmaidientu24h/?page_id=19',0,'page','',0);

/*Table structure for table `wp_term_relationships` */

DROP TABLE IF EXISTS `wp_term_relationships`;

CREATE TABLE `wp_term_relationships` (
  `object_id` bigint(20) unsigned NOT NULL DEFAULT '0',
  `term_taxonomy_id` bigint(20) unsigned NOT NULL DEFAULT '0',
  `term_order` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`object_id`,`term_taxonomy_id`),
  KEY `term_taxonomy_id` (`term_taxonomy_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

/*Data for the table `wp_term_relationships` */

insert  into `wp_term_relationships`(`object_id`,`term_taxonomy_id`,`term_order`) values (1,1,0),(6,1,0),(7,1,0),(7,2,0),(16,1,0),(16,2,0),(17,1,0),(17,2,0),(18,1,0),(18,3,0);

/*Table structure for table `wp_term_taxonomy` */

DROP TABLE IF EXISTS `wp_term_taxonomy`;

CREATE TABLE `wp_term_taxonomy` (
  `term_taxonomy_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `term_id` bigint(20) unsigned NOT NULL DEFAULT '0',
  `taxonomy` varchar(32) NOT NULL DEFAULT '',
  `description` longtext NOT NULL,
  `parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `count` bigint(20) NOT NULL DEFAULT '0',
