<?php/** * The Template for displaying all single posts. * * @package BamBooDev * @author LongNC * @since Shop 1.0 */global $wp_query,$title;$pagenum = isset($_GET['page'])?$_GET['page']:1;//$cat = get_category_by_slug($catSlug);$cat = get_queried_object();$title = $cat->cat_name;get_header();$posts = query_posts( array('post_type' => 'product','cat' => $cat->cat_ID, 'orderby' => 'created', 'order' => 'ASC','posts_per_page' => POSTS_PER_PAGE,'paged' => $pagenum));$htmlPaging =  paginate_links( array(	'total' => $wp_query->max_num_pages,	'base' => @add_query_arg('page','%#%'),	'format' => '',	'current' => $pagenum) );?>	<?php get_sidebar('left'); ?><div style="float: right; width: 770px;"><div id="slider">	<img src="<?php echo get_template_directory_uri(); ?>/images/slider.jpg"/></div><div style="float: left; width: 100%; margin-top: 5px;">	<div id="center">		<h3><?php echo $title?></h3>		<?php		foreach($posts as $post) {			$image = wp_get_attachment_image_src( get_post_thumbnail_id($post->ID),'thumbnail', 'single-post-thumbnail' );		?>		<a class="product_item" href="<?php echo get_permalink( $post->ID );?>" title="<?php echo $post->post_title?>">			<span class="title"><?php echo $post->post_title?></span>			<img width="150" height="150" alt="<?php echo $post->post_title?>" src="<?php echo $image[0]?>">		</a>		<?php		}		?>		<div class="paging"><?php echo $htmlPaging;?></div>	</div>	<?php get_sidebar('right'); ?></div></div><?php get_footer(); ?>