<?phpclass Customer {	public static function findByEmail($email) {		global $wpdb;		$query = $wpdb->prepare('SELECT t0.*,t1.`firstname`,t1.`lastname`,CONCAT(t1.firstname,\' \',t1.lastname) AS fullname,t1.`phone`,t1.`zipcode`,t1.`address`,t1.`city`,t1.`country` FROM `customers` t0 LEFT JOIN `address` t1 ON t0.`address_id`=t1.`id` WHERE t0.`email` = %s',$email);		return $wpdb->get_row($query,ARRAY_A);	}	public static function findShippingInfo($customer_id) {		global $wpdb;		$query = $wpdb->prepare('SELECT * FROM `address` WHERE `status` = 1 AND `customer_id` = %d',$customer_id);		return $wpdb->get_row($query,ARRAY_A);	}	public static function findAddressById($id) {		global $wpdb;		$query = $wpdb->prepare('SELECT * FROM `address` WHERE `status` = 1 AND `id` = %d',$id);		return $wpdb->get_row($query,ARRAY_A);	}	public static function findById($id) {		global $wpdb;		$query = $wpdb->prepare('SELECT t0.*,t1.`firstname`,t1.`lastname`,CONCAT(t1.firstname,\' \',t1.lastname) AS fullname,t1.`phone`,t1.`zipcode`,t1.`address`,t1.`city`,t1.`country` FROM `customers` t0 LEFT JOIN `address` t1 ON t0.`address_id`=t1.`id` WHERE t0.`id` = %d',$id);		return $wpdb->get_row($query,ARRAY_A);	}}