<?phpclass Customer {	public static function findByEmail($email) {		global $wpdb;		$query = $wpdb->prepare('SELECT * FROM `customers` WHERE `email` = %s',$email);		return $wpdb->get_row($query,ARRAY_A);	}	public static function findShippingInfo($customer_id) {		global $wpdb;		$query = $wpdb->prepare('SELECT * FROM `address` WHERE `status` = 1 AND `customer_id` = %d',$customer_id);		return $wpdb->get_row($query,ARRAY_A);	}	}