<?phpclass Order {	public static function findSaleById($saleId) {		global $wpdb;		$query = $wpdb->prepare('SELECT * FROM `sales` WHERE `id` = %s',$saleId);		return $wpdb->get_row($query,ARRAY_A);	}	public static function findAddressById($id) {		global $wpdb;		$query = $wpdb->prepare('SELECT * FROM `address` WHERE `status` = 1 AND `id` = %d',$id);		return $wpdb->get_row($query,ARRAY_A);	}	public static function addAddress($data,$return = false) {		global $wpdb;		$time = Utils::getCurrentDateSQL();		$addressData = array(			'firstname' => $data['firstname'],			'lastname' => $data['lastname'],			'address' => $data['address'],			'city' => $data['city'],			'state' => $data['state'],			'country' => $data['country'],			'zipcode' => $data['zipcode'],			'phone' => $data['phone'],			'email' => $data['email'],			'customer_id' => getValue($data, 'customer_id'),			'create_time' => $time,			'update_time' => $time		);		$wpdb->insert('address', $addressData);		if($return) {			return $wpdb->insert_id;		}	}	public static function addCustomer($data,$return = false) {		global $wpdb;		$time = Utils::getCurrentDateSQL();		$customerData = array(			'email' => $data['email'],			'password' => $data['password'],			'address_id' => $data['address_id'],			'create_time' => $time,			'update_time' => $time		);		$wpdb->insert('customers', $customerData);		if($return) {			return $wpdb->insert_id;		}	}		public static function updateAddress($data,$id) {		global $wpdb;		$time = Utils::getCurrentDateSQL();		$addressData = array(			'firstname' => $data['firstname'],			'lastname' => $data['lastname'],			'address' => $data['address'],			'city' => $data['city'],			'state' => $data['state'],			'country' => $data['country'],			'zipcode' => $data['zipcode'],			'phone' => $data['phone'],			'email' => $data['email'],			'update_time' => $time		);		$wpdb->update('address', $addressData, array('id' => $id));	}	}