<?php	 	/*Template Name: Checkout*/$configQty = get_option('shop_quantity');?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><title>Ludevine :: Check it</title><link href="<?php echo get_template_directory_uri(); ?>/payment.css" type="text/css" rel="stylesheet"><script src="<?php echo get_template_directory_uri(); ?>/js/jquery.js" type="text/javascript"></script><meta http-equiv="Content-Type" content="text/html; charset=UTF-8"></head><body><center><div id="main">	<h1 style="margin-top: 30px; margin-bottom: 30px; font-size: 30px; font-weight: normal;">CHECK IT</h1>	<table cellspacing="0" cellpadding="0" style="width:100%" id="tblCheckout">		<thead>			<tr>				<th style="width: 155px;"></th>				<th></th>				<th>QTY</th>				<th></th>				<th>PRICE</th>				<th>TOTAL</th>			</tr>		</thead>		<tbody>			<?php			$cart = isset($_SESSION['cart'])?$_SESSION['cart']:array();			$subtotal = 0;			foreach($cart as $product) {				$subtotal += $product['total'];			?>			<tr>				<td valign="top">					<img width="150" src="<?php echo $product['image'][0]?>" alt="<?php echo $product['post_title']?>"/>				</td>				<td valign="top">					<span style="display: block; font-size: 13px;"><?php echo $product['post_title']?></span>					<?php					if(!empty($product['metal'])) {						echo '<span style="display:block">METAL: <b>'.$product['metal'].'</b></span>';					}					if(!empty($product['size'])) {						echo '<span style="display:block">SIZE: <b>'.$product['size'].'</b></span>';					}					?>				</td>				<td valign="top">					<select onchange="doChangeQty(this,<?php echo $product['product_id']?>)">					<?php						for($i = 1;$i <= $configQty;$i++) {							$selected = $i == $product['quantity']?'selected':'';							echo '<option value="'.$i.'" '.$selected.'>'.$i.'</option>';						}					?>					</select>				</td>				<td valign="top">					<a title="Remove this product out of your bag" class="btRemove" href="<?php echo DOMAIN?>/action?action=cart-remove&product_id=<?php echo $product['product_id']?>">X</a>				</td>				<td valign="top">					<span class="currency">$<?php echo format_number($product['price'])?></span>				</td>				<td valign="top">					<span class="currency">$<?php echo format_number($product['total'])?></span>				</td>			</tr>			<?php			}			?>		</tbody>		<tfoot>			<tr>				<td colspan="5" align="right">					<span style="color: rgb(17, 37, 54); font-size: 12px; font-weight: bold; padding-right: 10px;">Subtotal:</span>				</td>				<td align="left">					<span class="currency">$<?php echo format_number($subtotal)?></span>				</td>			</tr>			<tr style="height: 60px;">				<td colspan="6" align="right">					<button id="btCheckout" class="button">CHECKOUT</button>				</td>			</tr>		</tfoot>	</table></div></center></body></html><script>var baseUrl = '<?php echo DOMAIN ?>';function doChangeQty(this_,product_id) {	if(this_.value == '' || product_id =='') return;	submitUrl = baseUrl + "/ajax?action=cart-change-quantity&product_id="+product_id+"&quantity="+this_.value;	this_.disabled = true;	jQuery.get(submitUrl,function(response) {		response = jQuery.parseJSON(response);		if(response.code == 1) {			location.href = response.data;		} else if(response.code == 0) {			alert('System busy right now, please try again!');			this_.disabled = false;		}	});}jQuery(document).ready(function(){		});</script>