<?php/*Template Name: Collections*/get_header(); $args = array(   'post_type' => 'attachment',   'numberposts' => -1,   'post_status' => null,   'orderby' => 'menu_order',   'order'          => 'ASC',   'post_parent' => $post->ID);$attachments = get_posts( $args );?>	<script src="<?php echo get_template_directory_uri(); ?>/js/jquery-ui.js" type="text/javascript"></script><script src="<?php echo get_template_directory_uri(); ?>/js/jquery-window/jquery.window.min.js" type="text/javascript"></script><link href="<?php echo get_template_directory_uri(); ?>/js/jquery-window/css/jquery.window.css" type="text/css" rel="stylesheet"><img class="bg" src="<?php bloginfo('template_url'); ?>/images/bg/collections.jpg">	<div id="horizontal_slider">	<table cellpadding="0">		<tr>			<td>				<h1 style="font-size: 40px; font-style: italic; padding: 20px 30px; text-align: center;"><?php	 	echo $post->post_title?></h1>				<div style="padding-left: 23px; padding-right: 20px; font-style: italic; text-align: center; width: 250px;">				<?php				$content = $post->post_content;				$content = apply_filters('the_content', $content);				$content = str_replace(']]>', ']]&gt;', $content);				echo $post->post_content;				?>				</div>			</td>			<?php			if ( $attachments ) {			foreach ( $attachments as $attachment ) {				$image = wp_get_attachment_image_src( $attachment->ID,'large' );				$attachment_metadata = get_post_meta($attachment->ID, '_wp_attachment_metadata',true);				$image_link = '';				if(isset($attachment_metadata['image_meta']['image_link'])) {					$image_link = $attachment_metadata['image_meta']['image_link'];				}				?>			<td>				<img href="<?php echo $image_link?>" title="<?php echo $attachment->post_title ?>" src="<?php	 	 echo $image[0]?>" width="<?php	 echo $image[1]?>" style="max-height:480px"/>			</td>				<?php	 				}}			?>			<td>				<span id="back"></span>			</td>		</tr>	</table></div><?php	 	 get_footer(); ?><script>$(document).ready(function(){		$("#horizontal_slider img").live("click",function(){		//var scrollLeft = jQuery("#horizontal_slider").scrollLeft();		//jQuery("#horizontal_slider").animate({scrollLeft: scrollLeft + jQuery(this).offset().left }, 1000);		var image_link = $(this).attr("href");		if(image_link != '') {			$.window({				title: "",				url: image_link,				width: 960,				height:500,				showModal: true,				resizable: true,				scrollable: true,				minimizable: false,				maximizable: true, 				bookmarkable: false,				scrollable:true,				showFooter : false,				showRoundCorner : true,				y:10,				onClose: function () {				},				onOpen : function(){					//$("body").css("overflow", "hidden");				},				onIframeEnd: function() {					var height = $("iframe").contents().find("body").height();					$("iframe").css("height",height+50+"px");					$("div.window_panel").css("height",height+50+"px");				}			});		}	});	$("#horizontal_slider #back").live("click",function(){		$("#horizontal_slider").animate({scrollLeft: 0}, 700);	});});</script>